exports.command = function(expected, message) 
{
	var self = this;

	self.title(
		function(result) {
            var test;
            if (expected.test) {
                test = expected.test(result.value);
            } else {
                test = result.value === expected;
            }
			self.showTest(test, result.value, expected, message);
		}
	);
	
};

